    def ogrEkle(self):
        mycursor = veritab.cursor()
        
        ogrAd = self.kytOgrAd.text()
        ogrSoy = self.kytOgrSoy.text()
        ogrNo = self.kytOgrNo.text()
        ogrSnf = self.kytOgrSnf.currentText()
        ogrSube = self.kytOgrSube.currentText()

        sorgu = "INSERT INTO ogrenciler (ad, soyad, numara, sinif, sube) VALUES (%s, %s, %s, %s, %s)"
        degerler = (str(ogrAd), str(ogrSoy), str(ogrNo), str(ogrSnf), str(ogrSube))
        mycursor.execute(sorgu, degerler)
        veritab.commit()
        print(mycursor.rowcount, "kayıt eklendi.")


    def ogrListele(self):
        mycursor = veritab.cursor()
        ogrAd = self.kytOgrAd.text()
        ogrSoy = self.kytOgrSoy.text()
        ogrNo = self.kytOgrNo.text()
        ogrSnf = self.kytOgrSnf.currentText()
        ogrSube = self.kytOgrSube.currentText()
        mycursor.execute("SELECT * FROM ogrenciler WHERE numara='"+ ogrNo +"' or (ad='"+ ogrAd +"' and soyad='"+ ogrSoy +"') or (sinif='"+ str(ogrSnf) +"' and sube='"+ str(ogrSube) +"')")                  

        result = mycursor.fetchall()

        self.bilgiListesi.setRowCount(0)

        for row_number, row_data in enumerate(result):
            self.bilgiListesi.insertRow(row_number)

            for column_number, data in enumerate(row_data):
                self.bilgiListesi.setItem(row_number, column_number, QTableWidgetItem(str(data)))

